INSERT INTO @tblTempTable ([SyndicatedContentId], [BravoVetsCountryId], [SyndicatedContentTypeId], [SyndicatedContentPostTypeId], [Title], [Author], [Subject], [Summary], [ContentText], [LinkUrl], [BravoVetsStatusId], [NumberOfFavorites], [NumberOfShares], [NumberOfViews], [PublishDateUtc], [CreateDateUtc], [ModifiedDateUtc], [Deleted]) VALUES ('379', '5', '2', '7', 'MANAGING A COMMUNITIES: How to Handle Questions', 'Merck', 'MANAGING A COMMUNITIES: How to Handle Questions', 'As social media becomes a bigger part of people’s everyday lives, many turn to it to ask questions of brands/companies, so you n', 'As social media becomes a bigger part of people’s everyday lives, many turn to it to ask questions of brands/companies, so you need to be prepared to answer and field various types of questions. Consider topics that are likely to be asked by your audience, from simple questions about your practice (What are the hours?) to more specific treatment/care questions (Why is my dog vomiting?). Be prepared by writing and maintaining guidelines for answering questions or guiding people to where they can find the answer. For example: “Hi [name], to find out more about our hours and how to set up an appointment, please see our website here: [link]. We look forward to meeting you and your pet!”', '', '4', '0', '0', '0', '2010-08-20 19:58:26.657', '2010-08-20 19:58:26.657', '2010-08-20 19:58:26.657', 'False')
INSERT INTO @tblTempTable ([SyndicatedContentId], [BravoVetsCountryId], [SyndicatedContentTypeId], [SyndicatedContentPostTypeId], [Title], [Author], [Subject], [Summary], [ContentText], [LinkUrl], [BravoVetsStatusId], [NumberOfFavorites], [NumberOfShares], [NumberOfViews], [PublishDateUtc], [CreateDateUtc], [ModifiedDateUtc], [Deleted]) VALUES ('380', '5', '2', '7', 'MANAGING A COMMUNITIES: Assign a Social Admin', 'Merck', 'MANAGING A COMMUNITIES: Assign a Social Admin', 'If possible, assign a trusted person or team in your practice to be responsible for keeping the social pages up-to-date as well ', 'If possible, assign a trusted person or team in your practice to be responsible for keeping the social pages up-to-date as well as monitoring your website. If you have no staff appropriate for this role, it is up to you to be diligent in checking your social pages at least once a day to handle the questions or comments. Social media is “on” 24/7, so keeping up with it regularly and in a timely manner is important.', '', '4', '0', '0', '0', '2010-08-19 19:58:26.657', '2010-08-19 19:58:26.657', '2010-08-19 19:58:26.657', 'False')
INSERT INTO @tblTempTable ([SyndicatedContentId], [BravoVetsCountryId], [SyndicatedContentTypeId], [SyndicatedContentPostTypeId], [Title], [Author], [Subject], [Summary], [ContentText], [LinkUrl], [BravoVetsStatusId], [NumberOfFavorites], [NumberOfShares], [NumberOfViews], [PublishDateUtc], [CreateDateUtc], [ModifiedDateUtc], [Deleted]) VALUES ('381', '5', '2', '7', 'MANAGING A COMMUNITIES: Encouraging Participation', 'Merck', 'MANAGING A COMMUNITIES: Encouraging Participation', 'When writing your posts, consider ways you can encourage conversation or further discussion about the topic. For example: “Durin', 'When writing your posts, consider ways you can encourage conversation or further discussion about the topic. For example: “During the hot summer months, make sure to refill your pet’s water bowl with cool water often. What other tips do you use to keep your pets cool?”<br><br>

			Keep it fun. Consider holidays or local events to spark fun conversation from your fans. Example: “Do you dress your pets up for Halloween? We’d love to see your photos!”', '', '4', '0', '0', '0', '2010-08-18 19:58:26.657', '2010-08-18 19:58:26.657', '2010-08-18 19:58:26.657', 'False')
INSERT INTO @tblTempTable ([SyndicatedContentId], [BravoVetsCountryId], [SyndicatedContentTypeId], [SyndicatedContentPostTypeId], [Title], [Author], [Subject], [Summary], [ContentText], [LinkUrl], [BravoVetsStatusId], [NumberOfFavorites], [NumberOfShares], [NumberOfViews], [PublishDateUtc], [CreateDateUtc], [ModifiedDateUtc], [Deleted]) VALUES ('382', '5', '2', '7', 'MANAGING A COMMUNITIES: Handling Issues', 'Merck', 'MANAGING A COMMUNITIES: Handling Issues', 'It is possible that you will have to deal with negative comments or posts on your page. Come up with a protocol for how to addre', 'It is possible that you will have to deal with negative comments or posts on your page. Come up with a protocol for how to address these if and when they arise and who handles urgent issues.', '', '4', '0', '0', '0', '2010-08-17 19:58:26.657', '2010-08-17 19:58:26.657', '2010-08-17 19:58:26.657', 'False')
INSERT INTO @tblTempTable ([SyndicatedContentId], [BravoVetsCountryId], [SyndicatedContentTypeId], [SyndicatedContentPostTypeId], [Title], [Author], [Subject], [Summary], [ContentText], [LinkUrl], [BravoVetsStatusId], [NumberOfFavorites], [NumberOfShares], [NumberOfViews], [PublishDateUtc], [CreateDateUtc], [ModifiedDateUtc], [Deleted]) VALUES ('383', '5', '2', '7', 'PR TOPICS: PR and Your Clinic', 'Merck', 'PR TOPICS: PR and Your Clinic', 'PR can be a great means to create wider awareness of your clinic as well as establish you, the clinician, as an expert in your f', 'PR can be a great means to create wider awareness of your clinic as well as establish you, the clinician, as an expert in your field. While the supplied materials are specific to flea and tick protection, the tips and resources provided successfully promote your practice and raise awareness of any number of subject areas. By sharing these messages, you will attract new clients to your doors and deepen relationships with existing clients—and you’ll be doing a great service for dogs and the families who own them!', '', '4', '0', '0', '0', '2010-08-16 19:58:26.657', '2010-08-16 19:58:26.657', '2010-08-16 19:58:26.657', 'False')
INSERT INTO @tblTempTable ([SyndicatedContentId], [BravoVetsCountryId], [SyndicatedContentTypeId], [SyndicatedContentPostTypeId], [Title], [Author], [Subject], [Summary], [ContentText], [LinkUrl], [BravoVetsStatusId], [NumberOfFavorites], [NumberOfShares], [NumberOfViews], [PublishDateUtc], [CreateDateUtc], [ModifiedDateUtc], [Deleted]) VALUES ('384', '5', '2', '7', 'PR TOPICS: How Will PR Help My Practice?', 'Merck', 'PR TOPICS: How Will PR Help My Practice?', 'With increased public interest in the importance of pet healthcare and especially flea and tick control, the local media may tur', 'With increased public interest in the importance of pet healthcare and especially flea and tick control, the local media may turn to you for commentary on how to protect companion animals. This offers a great opportunity to promote your practice and the services you offer. However, always exercise great care when working with the press. Successful media engagement can have a direct impact on your business; poor media relations skills can lead to lack of recognition for good work done.', '', '4', '0', '0', '0', '2010-08-15 19:58:26.657', '2010-08-15 19:58:26.657', '2010-08-15 19:58:26.657', 'False')
INSERT INTO @tblTempTable ([SyndicatedContentId], [BravoVetsCountryId], [SyndicatedContentTypeId], [SyndicatedContentPostTypeId], [Title], [Author], [Subject], [Summary], [ContentText], [LinkUrl], [BravoVetsStatusId], [NumberOfFavorites], [NumberOfShares], [NumberOfViews], [PublishDateUtc], [CreateDateUtc], [ModifiedDateUtc], [Deleted]) VALUES ('385', '5', '2', '7', 'PR TOPICS: What If I Have No Experience in Public Relations?', 'Merck', 'PR TOPICS: What If I Have No Experience in Public Relations?', 'If you’re new to PR, these tips will give you the materials you need to successfully start engaging with the media and other key', 'If you’re new to PR, these tips will give you the materials you need to successfully start engaging with the media and other key stakeholders—all you need to provide is the enthusiasm. Veterinarians are part of the fabric of any community and chances are that most journalists you’ll be speaking with are pet owners themselves and have a high regard for the work you do.<br><br>

			Once you start building relationships with journalists in your area, you’ll soon notice they’ll be calling on you unprompted for commentary on a variety of pet stories they’re working on—and that publicity is a wonderful avenue for growing your practice.', '', '4', '0', '0', '0', '2010-08-14 19:58:26.657', '2010-08-14 19:58:26.657', '2010-08-14 19:58:26.657', 'False')
INSERT INTO @tblTempTable ([SyndicatedContentId], [BravoVetsCountryId], [SyndicatedContentTypeId], [SyndicatedContentPostTypeId], [Title], [Author], [Subject], [Summary], [ContentText], [LinkUrl], [BravoVetsStatusId], [NumberOfFavorites], [NumberOfShares], [NumberOfViews], [PublishDateUtc], [CreateDateUtc], [ModifiedDateUtc], [Deleted]) VALUES ('386', '5', '2', '7', 'PR TOPICS: Getting Started', 'Merck', 'PR TOPICS: Getting Started', 'Do Your Research<br>
			- Ensure that you are well prepared before initiating media outreach. If you have never engaged with th', 'Do Your Research<br>
			- Ensure that you are well prepared before initiating media outreach. If you have never engaged with the media, start by preparing a list of local newspapers, free sheets, local magazines, radio, television stations, websites and e-newsletters.  Conducting online research will help you in this regard, but chances are you know most of them already.  <br><br>

			- Once you have identified the appropriate news outlets for a story idea or press release, take time to familiarize yourself with the reporters or editors who cover companion animals. Buy a copy of their publication or read their prior stories online. In many cases, it’s even possible to download a “Media Pack” from each media outlet you would like to contact, which will provide you with details on editorial guidelines and submission deadlines (these are different than advertising deadlines). If you can’t get this information, telephone the publication. Most weekly news outlets need information seven days in advance, but monthlies often require a six-week or three-month lead time. Understanding precise deadlines will allow you to better plan when to send out your media information.
			<br><br><br>
			Take Action<br>
			- When you are ready, call the relevant journalist. Introduce yourself as the point of contact and explain the aim of the program. Highlight the main objective, which in this instance is to raise awareness of the importance of year-round flea and tick protection. If possible, it’s always helpful to have facts and figures at your fingertips when discussing this issue.  From the kinds of fleas and ticks that are most prevalent in your area to seasonal spikes in incidence rates, this intelligence can help reporters shape a powerful story and emphasize your points (see CICADA if available in your country, or local testing laboratory results for monitoring outbreaks in your area).<br><br>

			Inform Your Staff<br>
			- During periods of media outreach, make sure that everyone in your practice stays in the loop. Consider training your receptionist to take inbound calls from journalists, ensuring they ask the right sort of questions so you can follow up with more of the details. These should include:<br>
			<br>
			The name of the journalist and what outlet they’re reporting for<br>

			 What subject/information they are most interested in talking about<br>

			Their contact details<br>

			The deadline for a response<br>

			Identify in advance a spokesperson within your practice that will be in charge of replying to press requests and conducting media interviews. Always have a backup person for any absences.<br>
			<br>', '', '4', '0', '0', '0', '2010-08-13 19:58:26.657', '2010-08-13 19:58:26.657', '2010-08-13 19:58:26.657', 'False')
INSERT INTO @tblTempTable ([SyndicatedContentId], [BravoVetsCountryId], [SyndicatedContentTypeId], [SyndicatedContentPostTypeId], [Title], [Author], [Subject], [Summary], [ContentText], [LinkUrl], [BravoVetsStatusId], [NumberOfFavorites], [NumberOfShares], [NumberOfViews], [PublishDateUtc], [CreateDateUtc], [ModifiedDateUtc], [Deleted]) VALUES ('387', '5', '2', '7', 'PR TOPICS: Why Now?', 'Merck', 'PR TOPICS: Why Now?', 'The launch of BRAVECTO offers you an opportunity to initiate an important conversation with all of these audiences about the imp', 'The launch of BRAVECTO offers you an opportunity to initiate an important conversation with all of these audiences about the importance of year-round flea and tick protection that will inspire regular, happy checkups at your practice.', '', '4', '0', '0', '0', '2010-08-12 19:58:26.657', '2010-08-12 19:58:26.657', '2010-08-12 19:58:26.657', 'False')
INSERT INTO @tblTempTable ([SyndicatedContentId], [BravoVetsCountryId], [SyndicatedContentTypeId], [SyndicatedContentPostTypeId], [Title], [Author], [Subject], [Summary], [ContentText], [LinkUrl], [BravoVetsStatusId], [NumberOfFavorites], [NumberOfShares], [NumberOfViews], [PublishDateUtc], [CreateDateUtc], [ModifiedDateUtc], [Deleted]) VALUES ('388', '5', '2', '7', 'PR TOPICS: Working With the Media', 'Merck', 'PR TOPICS: Working With the Media', 'To be effective in talking to the media, you need to remain focused on the key messages you are trying to put across.  These mes', 'To be effective in talking to the media, you need to remain focused on the key messages you are trying to put across.  These messages should be included in all the materials you deliver. The points below will help you keep focused on the main elements to communicate (you may develop others as your program evolves):<br>
			<br>
			Much like oral care, vaccinations and diet, flea and tick products should be part of the year-round health regimen for your dog.<br>

			Pet owners often forget to administer their monthly flea and tick products.<br>

			Fleas and ticks are not just confined to the countryside or suburbs. They’re a problem in cities, too. <br>

			Effective flea and tick treatments should be used in all dogs eight weeks old and older, and weighing 4.4 pounds (1.9 kg) or more.<br>

			Pet owners should provide their breeding, pregnant and lactating female dogs with a safe and effective flea and tick treatment.<br>
			<br>', '', '4', '0', '0', '0', '2010-08-11 19:58:26.657', '2010-08-11 19:58:26.657', '2010-08-11 19:58:26.657', 'False')
INSERT INTO @tblTempTable ([SyndicatedContentId], [BravoVetsCountryId], [SyndicatedContentTypeId], [SyndicatedContentPostTypeId], [Title], [Author], [Subject], [Summary], [ContentText], [LinkUrl], [BravoVetsStatusId], [NumberOfFavorites], [NumberOfShares], [NumberOfViews], [PublishDateUtc], [CreateDateUtc], [ModifiedDateUtc], [Deleted]) VALUES ('389', '5', '2', '7', 'PR TOPICS: Media Dos and Don’ts', 'Merck', 'PR TOPICS: Media Dos and Don’ts', 'Do<br>
			<br>
			Be clear about the importance of year-round flea and tick protection.<br>
			Be prepared for media interest', 'Do<br>
			<br>
			Be clear about the importance of year-round flea and tick protection.<br>
			Be prepared for media interest and be ready to manage it.<br>
			Select a spokesperson from your practice to talk to the media.<br>
			Ensure you are familiar with your questions and answers well in advance.<br>
			Know the angle which is most likely to interest the type of media you are talking to, and stick to it.<br>
			Be helpful and efficient in your response to media enquiries.<br>
			Make sure you supply information within the deadlines they specify. If you can’t meet the deadline, make sure the relevant journalist knows when you will be able to get the information to them.<br>
			Be polite and honest and fully prepared to answer the questions.<br>
			If you don’t know the answer to a question, say so, then find out the information and go back to the journalist.<br>
			<br>
			Don’t<br>
			<br>
			Don’t speculate or make predictions for the future. Stick to the facts you have.<br>
			Don’t provide information that you are not happy to be made public. Don’t try “off the record.”<br>
			Don’t sacrifice long-term relationships for short-term results.<br>
			<br>', '', '4', '0', '0', '0', '2010-08-10 19:58:26.657', '2010-08-10 19:58:26.657', '2010-08-10 19:58:26.657', 'False')
INSERT INTO @tblTempTable ([SyndicatedContentId], [BravoVetsCountryId], [SyndicatedContentTypeId], [SyndicatedContentPostTypeId], [Title], [Author], [Subject], [Summary], [ContentText], [LinkUrl], [BravoVetsStatusId], [NumberOfFavorites], [NumberOfShares], [NumberOfViews], [PublishDateUtc], [CreateDateUtc], [ModifiedDateUtc], [Deleted]) VALUES ('390', '5', '2', '7', 'ALL ABOUT ANALYTICS: Measuring Facebook – Understanding Facebook Insights', 'Merck', 'ALL ABOUT ANALYTICS: Measuring Facebook – Understanding Facebook Insights', 'As a small business page on Facebook, you have access to the metrics, or analysis, of your page. Facebook Insights provide measu', 'As a small business page on Facebook, you have access to the metrics, or analysis, of your page. Facebook Insights provide measurements on your page''s performance and are available after at least 30 people like your page. Knowing the metrics, then, is in essence listening to your audience. <br><br>

			Facebook offers a plethora of information in your Insights dashboard. As you’re starting out, here are the most important metrics to pay attention to: fan growth, reach, engagement (likes, comments, shares) and when your fans are online.<br><br>

			Once you get more comfortable with using Facebook, you can use Insights to dig deeper into your page’s performance.
			Page Insights Help: <a href="https://www.facebook.com/help/247808452010769" target="_blank">https://www.facebook.com/help/247808452010769</a>. <br><br>

			What the numbers mean<br>
			<br>
			Fan growth – This is simply how many net new likes your page is gaining. Pay attention to spikes in new likes, i.e., was there a particular action you took that increased your likes?<br>
			Reach – The number of people who saw your post, either in their newsfeed or on your page. You may find that some post types receive more reach than others.<br>
			Engagement – This includes actions people take on your posts, including likes, comments, shares and clicks. Use these metrics to compare your different types of posts to determine which are doing best for your page and are resonating the most with your fans.<br>
			When your fans are online – Found under the Posts tab, this chart tells you when your fans are seeing your content. Pay attention to this so you can find the best times and days of the week to post your content.<br>
			<br>', '', '4', '0', '0', '0', '2010-08-09 19:58:26.657', '2010-08-09 19:58:26.657', '2010-08-09 19:58:26.657', 'False')
INSERT INTO @tblTempTable ([SyndicatedContentId], [BravoVetsCountryId], [SyndicatedContentTypeId], [SyndicatedContentPostTypeId], [Title], [Author], [Subject], [Summary], [ContentText], [LinkUrl], [BravoVetsStatusId], [NumberOfFavorites], [NumberOfShares], [NumberOfViews], [PublishDateUtc], [CreateDateUtc], [ModifiedDateUtc], [Deleted]) VALUES ('391', '5', '2', '7', 'ALL ABOUT ANALYTICS: Measuring Twitter', 'Merck', 'ALL ABOUT ANALYTICS: Measuring Twitter', 'Unfortunately, Twitter does not yet offer an insights dashboard like Facebook. However, Twitter’s simple metrics means it’s fair', 'Unfortunately, Twitter does not yet offer an insights dashboard like Facebook. However, Twitter’s simple metrics means it’s fairly easy to see how your page is doing manually.
			Once your Twitter account starts to take off, consider using one of the many free tools available to easily track your performance:<br><br>
			<a href="http://socialmediatoday.com/emoderation/1620486/beginner-s-guide-twitter-business-choosing-free-twitter-see-more-httpwwwemoderat" target="_blank">http://socialmediatoday.com/emoderation/1620486/beginner-s-guide-twitter-business-choosing-free-twitter-see-more-httpwwwemoderat</a>.<br><br>

			What the numbers mean<br><br>
			Followers – Like Facebook Likes, these are the people following your page and receiving your updates in their feed.<br>
			Retweets – This means someone has shared your tweet to their followers.<br>
			Replies & Mentions – These occur when someone uses your Twitter handle in a tweet, either to reply directly to your tweet (reply) or simply mention your practice in a statement or question (mention).<br>
			Favorite – Akin to a Facebook Like, this means someone enjoyed your tweet enough to call it a Favorite.<br><br>', '', '4', '0', '0', '0', '2010-08-08 19:58:26.657', '2010-08-08 19:58:26.657', '2010-08-08 19:58:26.657', 'False')
