INSERT INTO @tblTempTable ([SyndicatedContentId], [BravoVetsCountryId], [SyndicatedContentTypeId], [SyndicatedContentPostTypeId], [Title], [Author], [Subject], [Summary], [ContentText], [LinkUrl], [BravoVetsStatusId], [NumberOfFavorites], [NumberOfShares], [NumberOfViews], [PublishDateUtc], [CreateDateUtc], [ModifiedDateUtc], [Deleted]) VALUES ('477', '6', '2', '5', 'Cómo gestionar las preguntas', 'Merck', 'Cómo gestionar las preguntas', 'A medida que diariamente las redes sociales se convierten en una parte más importante de la vida de las personas, muchos las uti', 'A medida que diariamente las redes sociales se convierten en una parte más importante de la vida de las personas, muchos las utilizan para hacer preguntas sobre marcas/empresas, de manera que has de estar preparado para contestar diversos tipos de preguntas. Considera temas sobre los que sea más probable que te pregunten, desde simples cuestiones de tu práctica (¿Cuál es tu horario?)  hasta preguntas más especificas sobre tratamientos/cuidados (¿Por qué vomita mi perro?). Estate preparado, escribe y mantén actualizada una guía para contestar preguntas o una lista de personas a las que se pueda preguntar. Por ejemplo: “Hola [nombre], para saber más sobre nuestro horario de atención y cómo solicitar una cita, por favor, consulta nuestra página web aquí: [vínculo]. Esperamos verte pronto, a ti y a tu mascota!”', '', '4', '0', '0', '0', '2010-05-14 19:58:26.657', '2010-05-14 19:58:26.657', '2010-05-14 19:58:26.657', 'False')
